mapper.select:
	 
select count(*)
  from user 
 where id = #{id}

	 
select no, name
  from user 
 where no = #{value }

	 
select no, name, email, gender
  from user 
 where email = #{value }

mapper.update:

update user
   set name = #{name },


	
	password=#{password }
	
		   

       gender = #{gender }
       
 where no = #{no }
